package Java;

public class Data_Types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Data Types
		//1.Primitive data types- it takes the space in the memory without object

		//Boolean type:true/false

		//Numeric types:

		//a. character type:char

		//b. Integral Values:

		      // b.1.:Integer : byte, short , int, long

		       //b.2. :Floatinf-points :float, double

		//2.non- primitive data types:-with object. Array ,string , classes, interface, --oop

		
		//1.byte:  
		
		//size: 1 byte = 8 bits
		//range:-127 to 128
		byte b = 10;
		byte b1= 20;
		
		//2. short:
		
		//size 2 byte =16 bits
		//range
		short s= 234;
		short s1=1;
		
		
		//3. int:
	
		//size= 4 bytes =32 bits
		//range :
		
		int i = 2300;
		int j= 23444;
		
		
		//4. long
		
		//size : 8 bytes= 63 bits
		
		long num =12345678765432l;
		long distance = 224242593259325l;
		
		
		//5. float:
		 
		//size : 4 bytes = 32 bits
		//range : after . it can take upto 7 digits
		 float f1= 12.33f;
		 float f2= (float)34.56;
		 float f3= 100;
		 
		 
		 //6. double:
		 
		 //size; 8 bytes =64 bits
		 //range : after . it can take upto 15 digits
		 double d= 12.3333;
		 
		 
		 //7. char:
		 char c1='a'; //a-z, A-Z, 0-9, all special chars
		 char c3='A';
		 char c4='1';
		 char c5= '*';
		 
		 //8. boolean;
		 
		 //size; ~ 1 bit
		 
		 boolean flag = true;
		 boolean test= false;
		  
		 
		
		


	}

}
